import mongoose from "mongoose";

const DonorSchema = new mongoose.Schema({
    firstName: {
        type: String,

    }
    , lastName: {
        type: String,

    },
    email: {
        type: String,
        unique:true,
        lowercase: true,
        trim: true,

    },
    contactNo: {
        type: Number,

    },
    address: {
        type: String,
    },
    city: {
        type: String,

    },
    pincode: {
        type: Number,
        minlength: 6,
        maxlength: 10
    },
    password: {
        type: String,
        minlength: [8, "Password should be at least 8 characters"],

    }
})

const DonorModel = mongoose.model("Donor", DonorSchema)

export { DonorModel as Donor }